/*
	Left sidebars plugin
*/

(function($){

	// Defining our jQuery plugin
	
	$.fn.left_sidebar = function() {

		return this.click(function(e) {

	        e.preventDefault();

	        // Get element ID to open
	        var sbToOpen = $(this).attr('data-target');


	        if ($(this).attr('data-sb-state') == "sb-closed") {

	            // If page about - configure nav tabs width
	            if ($(".nav-tabs-about").length) {
	                $('.nav-tabs-about').addClass('sidebar--opened');
	            };

	            set_sidebar_handlers();
	            $(this).html('CLOSE <i class="fa fa-caret-up"></i>');

	            // Make block to open ontop with z-index
	            switch (sbToOpen) {
	                case "side-bonus":
	                    $('#side-bonus').css('z-index', '101');
	                    $('#side-accent').css('z-index', '100');
	                    // $( this )
	                    break;
	                case "side-accent":
	                    $('#side-accent').css('z-index', '101');
	                    $('#side-bonus').css('z-index', '100');
	                    break;
	                default:
	                    console.log("block doesn't exist");
	            }

	            // Now open block
	            $(this).attr('data-sb-state', 'sb-opened');
	            $('#' + sbToOpen).addClass('sb-opened');
	            $('.content-container').addClass('sb-opened');

	            // Add class handler-opened
	            $('.sidebar-handler').removeClass('handler-opened');
	            $(this).addClass('handler-opened');

	        } else {

	            // If page about - configure nav tabs width
	            if ($(".nav-tabs-about").length) {
	                $('.nav-tabs-about').removeClass('sidebar--opened');
	            };

	            // Hide all sidebars
	            $('.sidebar-container').removeClass('sb-opened');
	            $('.content-container').removeClass('sb-opened');

	            // Set sidebar-handlers as closed
	            $('.sidebar-handler').each(function() {
	                $(this).attr('data-sb-state', 'sb-closed');
	            });

	            set_sidebar_handlers();

	            // Remove class handler-opened
	            $('.sidebar-handler').removeClass('handler-opened');
	        }
	    });

		function set_sidebar_handlers() {

		    $('.sidebar-handler').each(function() {
		        if ($(this).attr('data-target') == 'side-bonus') {
		            $(this).html('BONUS <i class="caret"></i>');
		        } else {
		            $(this).html('ACCENT <i class="caret"></i>');
		        }
		    });

		}

		return this;
	};
	
})(jQuery);


/*
	Loader overlay
*/

(function($){
	
	$.fn.loader_overlay = function() {

	    this.click(function() {
	        $('.loader-overlay').addClass('active');

	        setTimeout(hideOverlay, 1500);
	    });

	    function hideOverlay() {
	    	$('.loader-overlay').removeClass('active');
	    }

		return this;
	};
	
})(jQuery);


/*
	Left sidebars menu toggler
*/

(function($){
	
	$.fn.left_menu = function() {

	    return this.click(function() {
	        $(this).toggleClass('sm-active');
	        $(this).siblings('ul').slideToggle();
	    });

		return this;
	};
	
})(jQuery);



/*
 * Main Mobile menu
 * Simple mobile menu
 *
 *
*/
(function ( $ ) {
 
    $.fn.smplmnu = function(options) {

        //settings
        var settings = $.extend({
            // These are the defaults.
            background: "#EAD636",
            speed: "0.5s",
            textColor: "#fff"
        }, options );


        //vars
        var hittrigger = $(this);
        var clspos = hittrigger.next('ul');
        var menutextcolor = hittrigger.next('ul li a');
        var overllay = '<div class="overally"></div>';

        //manipulating dom
        hittrigger.addClass('inrwrpr');
        clspos.addClass('inrwrpr');
        $('.inrwrpr').wrapAll( "<div class='navwrp'>" );
        // clspos.prepend('<a href="javascript:void(0)" class="mnuclose ion-close-round"><span>X</span></a>');
        $('body').prepend(overllay);
        
        
        //functions and methods
        hittrigger.click(function(event){
            $('.mobimenu').toggleClass('mnuopn');
            $('.mnuopn a').css({
                'color': settings.textColor
            });
            $('.overally').toggleClass('ovrActv');
        });

        $('.mnuclose').click(function(event) {
            $('.mobimenu').removeClass('mnuopn');
            $('.overally').removeClass('ovrActv');
            $('.smobitrigger').removeClass('muclose');
        });

        $('.overally').click(function(event) {
            $('.mobimenu').removeClass('mnuopn');
            $(this).removeClass('ovrActv');
            $('.smobitrigger').removeClass('is-active');
        });

        return this             
 
    };
 
}( jQuery ));



